BEGIN
	SET NOCOUNT ON;
	
	DECLARE @DisableFunctionality INT = (SELECT COUNT(*) FROM Equipment e WHERE e.RefNo=@SystemName AND e.EquipmentcategoryID=19 AND MobileUnitLicenceKeyID IS NOT NULL)

