                           FROM
                                  @IDList
                           GROUP BY
                                  SampleID,RoomGUID,ItemGUID,ItemGUIDVersion
                     ) _s
					 LEFT OUTER JOIN JobReinspectionStructureData jrsd WITH (NOLOCK) ON jrsd.SampleID=_s.SampleID AND jrsd.JobReinspectionStructureID=@JobReinspectionStructureID
                     WHERE
