ALTER PROC [dbo].[enterprise_DataTransfterList]
(
	@EnterpriseDataTransferID INT = NULL,
	@ShowCurrentTransfer BIT = 0
)
AS
BEGIN
	
	SET NOCOUNT ON;
	
	SELECT
		TOP 1
		ent.JobID,
		st.SurveyTypeID,
		config.s__ServerCode [ServerCode],
		ent.EnterpriseServerCode,
		ent.TEAMSUPRN,
		edt.EnterpriseDataTransferID,
		edt.DateCreated,
		edt.DateStarted,
		IVF_ImportGroupID
	FROM
		(
			SELECT
				j.JobID,
				j.Approved,
				cs.EnterpriseServerCode,
				cs.TEAMSUPRN,
				ROW_NUMBER() OVER (PARTITION BY j.JobID ORDER BY a.StartTime DESC) [Identifier]
			FROM
				(
					Select 
						c.ClientID, si.SiteID, c.EnterpriseServerCode, si.TEAMSUPRN
					FROM
						Client c WITH (NOLOCK)
						INNER JOIN ClientSite cs WITH (NOLOCK) ON c.ClientID = cs.ClientID
						INNER JOIN Site si WITH (NOLOCK) ON cs.SiteID = si.SiteID
					WHERE
						c.EnterpriseServerCode IS NOT NULL
							AND
						NULLIF(si.TEAMSUPRN,'NULL') IS NOT NULL
				) cs
				INNER JOIN Job j WITH (NOLOCK) ON j.ClientID=cs.ClientID AND j.SiteID=cs.SiteID
				INNER JOIN Quote q WITH (NOLOCK) ON j.JobID = q.JobID
				INNER JOIN Appointment a WITH (NOLOCK) ON a.QuoteID=q.QuoteID AND a.DateDeclined IS NULL
			WHERE
				j.Approved IS NOT NULL
		) ent
		INNER JOIN JobEmployee je WITH (NOLOCK) ON ent.JobID=je.JobID
		INNER JOIN Register r WITH (NOLOCK) ON r.JobEmployeeID=je.JobEmployeeID
		INNER JOIN Survey st WITH (NOLOCK) ON r.SurveyID = st.SurveyID
		LEFT OUTER JOIN EnterpriseDataTransfer edt WITH (NOLOCK) ON edt.JobID = ent.JobID
		CROSS APPLY config
	WHERE
		(
			CASE WHEN edt.EnterpriseDataTransferID IS NOT NULL THEN
				CASE WHEN edt.EnterpriseServerCode=ent.EnterpriseServerCode AND edt.TEAMSUPRN=ent.TEAMSUPRN THEN 1 ELSE 0 END
			ELSE
				1
			END = 1
		)
			AND
