			AND
		(
			CASE WHEN
				edt.DateFailed IS NULL
					AND
				edt.DateCompleted IS NULL
			THEN 1 ELSE 0 END  = 1
		) 
			AND
		(
			CASE WHEN @EnterpriseDataTransferID IS NULL THEN
				1
			ELSE
				CASE WHEN @EnterpriseDataTransferID = IsNull(edt.EnterpriseDataTransferID,-1) THEN 1 ELSE 0 END
			END = 1
		)
			AND
		(
			CASE WHEN @ShowCurrentTransfer = 0 THEN
